    })
  }

  render() {
    return (
      <Layout>
        <Title/>
          <input
            handleSubmit={this.handleSubmit}
            handleTaskChange={this.handleTaskChange}
            value={this.state.newTask}
          /> 
        <TaskList
          task={this.state.tasks}
        />
      </Layout>
    );
  }
}

export default App;
